import './App.css';
import { useState } from "react";
function App() {
  const brands = [
    { name: "Samsung", model: "s21 Ultra",
    specs: {
      os: 'Android',
      processor: 'Samsung Exynos 2100',
      storage: '256GB',
    }
  },

  ] ;
  return (
    <div className="App">
      {brands.map((brand)=> (<Vote brandName={brand.name} model={brand.model} specs={brand.specs}/>) )}

    </div>
  );
}
function Vote(props){
  const [like, setLike] = useState(0);
  const [dislike, setDislike] = useState(0);
  console.log("Props",props);
  return(
    <div className={'App'}
    style={{
      background: like >= dislike ? "white" : "crimson",
    }}>
      <h1 style={{textTransform: "uppercase"}}>{props.brandName} </h1>
      <h4>{props.model}</h4>
      <h2>Specs</h2>
      <ul>
        <li>{props.specs.os} </li>
        <li>{props.specs.processor}</li>
        <li>{props.specs.storage}</li>
      </ul>

      <button onClick={() => setLike(like + 1)}> {like}</button>
      <button onClick={() => setDislike(dislike + 1)}>{dislike}</button>


    </div>
  )
}

export default App;
