import './App.css';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { withStyles } from '@material-ui/core/styles';
import Badge from '@material-ui/core/Badge';
import { useState } from "react";
function App() {
  const abckart = [
    {
    image: "450 X 300",
    productType: 'Fancy Products',
    offerPrice: '$40.00 - $80.00',    
    },
    {
    image: "450 X 300",
    tag: "Sale",
    productType: 'Special Item',
    offerPrice:  '$20.00',
    price: '$18.00',
    rating: '⭐⭐⭐⭐⭐',   
    },
    {
    image: "450 X 300",
    tag: 'Sale',
    productType: 'Sale Item',
    offerPrice: '$50.00',
    price: '$25',  
    },
    {
    image: "450 X 300",
    productType: 'Popular Item', 
    offerPrice: '$40.00',
        rating: '⭐⭐⭐⭐⭐'  
      },
      {
        image: "450 X 300",
        tag: 'Sale',
        productType: 'Sale Item',
        offerPrice: '$50.00',
        price: '$25.00',

      },
      {
        image: "450 X 300",
        productType: 'Fancy Products',
        offerPrice: '$120.00 - $280.00',    
        },
        {
          image: "450 X 300",
          tag: 'Sale',
          productType: 'Special Item',
          offerPrice:  '$20.00',
          price: '$18.00',
          rating: '⭐⭐⭐⭐⭐',   
          },
          {
            image: "450 X 300",
            productType: 'Popular Item', 
            offerPrice: '$40.00',
                rating: '⭐⭐⭐⭐⭐'  
              },
  ];
  return (
    <div className="App">

       <IconButton aria-label="cart">
  <StyledBadge badgeContent={cart} color="secondary">
    <ShoppingCartIcon />
  </StyledBadge>
</IconButton>
      {abckart.map((abc) => (<Kart productImade={abc.image} product={abc.productType} price={abc.price} offerPrice={abc.offerPrice} rating = {abc.rating}/>))}

    </div>
  );
}
