import './App.css';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import { useState } from "react";
function App() {
  const brands = [
    { name: "Samsung", model: "s21 Ultra",
    specs: {
      os: 'Android',
      processor: 'Samsung Exynos 2100',
      storage: '256GB',
    }
  },
  { name: "Apple", model: "12 mini",
  specs: {
    os: 'iOS',
    processor: 'M21',
    storage: '256GB',
  }
  },
  { name: 'Realme', model: 'X7 Pro 5G',
  specs: {
    os: 'Android 11',
    processor: 'Dimensity 900',
    storage: '128GB',

  }

  }
  ] ;
  const abckart = [{
    image: "450 X 300",
    fancyProduct: 'Fancy Products',

    product:{
      fancyProduct:{


    },
      specialItem:{
        special: 'Special Item',
        price: '$18',
        rating: '⭐⭐⭐⭐⭐',
      },
      saleItem:{
        sale: 'Sale Item',
        price: '$25',
      },
      popularItem:{
        popular: 'Popular Item', 
        price: '$40',
        rating: '⭐⭐⭐⭐⭐'
      },
    },
  },
  ];
  return (
    <div className="App">
      {brands.map((brand)=> (<Vote brandName={brand.name} model={brand.model} specs={brand.specs}/>) )}
      {abckart.map((abc) => (<Kart productImade={abc.image} product={abc.product}/>))}

    </div>
  );
}
function Vote(props){
  const [like, setLike] = useState(0);
  const [dislike, setDislike] = useState(0);
  console.log("Props",props);
  return(
    <div className={'App'}
    style={{
      background: like >= dislike ? "smokewhite" : "crimson",
    }}>
      <h1 style={{textTransform: "uppercase"}}>{props.brandName} </h1>
      <h4>{props.model}</h4>
      <Specification specs={props.specs}/>


      <button onClick={() => setLike(like + 1)}> {like}</button>
      <button onClick={() => setDislike(dislike + 1)}>{dislike}</button>


    </div>
  )
}

function Specification(props){
  return(
    <div>
      <h2>Specs</h2>
      <ul>
        <li>{props.specs.os} </li>
        <li>{props.specs.processor}</li>
        <li>{props.specs.storage}</li>
      </ul>
    </div>
  )
}


const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

function Kart(item){
  const classes = useStyles();
  const [cart, setCart] = useState(0);
  return(
    // <div>
    //   <label>Cart </label><button>{cart} 🛒</button>
    //   <div>
    //   <h1>{item.productImade}</h1>
    //   </div>
    //   <h2>{item.product.fancyProduct.fancy}</h2>
    //   <h5>{item.product.fancyProduct.price}</h5>
    //   <button>View Products</button>
    // </div>

    <Card className={classes.root}>
    <CardActionArea>
      <CardMedia
        className={classes.media}
        image={item.productImade}
        title="Product Image"
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="h2">
          {item.product.fancyProduct.fancy}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
          across all continents except Antarctica
        </Typography>
      </CardContent>
    </CardActionArea>
    <CardActions>
      <Button size="small" color="primary">
        Add to Cart
      </Button>
    </CardActions>
  </Card>
  )

}

export default App;
