    {
    image: "450 X 300",
    productType: 'Popular Item', 
    price: '$40',
        rating: '⭐⭐⭐⭐⭐'  
      },
  ];
  return (
    <div className="App">
      {abckart.map((abc) => (<Kart productImade={abc.image} product={abc.productType} price={abc.price} rating = {abc.rating}/>))}

    </div>
