const useStyles = makeStyles({
  root: {
    width: 280,
    height: 400,
    textAlign: 'center',
  },
  media: {
    height: 170,
  },
});

const StyledBadge = withStyles((theme) => ({
  badge: {
    right: -3,
    top: 13,
    border: `2px solid ${theme.palette.background.paper}`,
    padding: '0 4px',
  },
}))(Badge);

function Kart(item){
  const classes = useStyles();
  const [cart, setCart] = useState(0);
  return(
    <div>
    <IconButton aria-label="cart">
  <StyledBadge badgeContent={cart} color="secondary">
    <ShoppingCartIcon />
  </StyledBadge>
</IconButton>
    <Card className={classes.root}>

    <CardActionArea>
      <CardMedia
        className={classes.media}
        image={item.productImade}
        title="Product Image"
      />
      <CardContent style={{height:'8rem'}}>
        <Typography gutterBottom variant="h5" component="h2" style={{fontWeight:'700'}}>
          {item.product}
        </Typography>
        <Typography >{item.offerPrice}</Typography>
        <Typography style={{ textDecorationLine: 'line-through' }}>{item.price}</Typography>
        <Typography>{item.rating}</Typography>
      </CardContent>
    </CardActionArea>

    <CardActions>
      <Button style={{marginBottom:'5px', alignItems:'center'}}  onClick={() => setCart(cart + 1)} variant="outlined" size="medium">
        Add to Cart
      </Button>
    </CardActions>

  </Card>
  </div>
  )

}

export default App;
